import './NewExpense.css'
import { useState } from 'react'

import ExpenseForm from './ExpenseForm'

const NewExpense = (newExpenseProps) => {
    const [isEditing, setIsEditing] = useState(false)

    const expenseFormEntryHandler = (enteredExpenseData) => {
        const expenseData = {
            // this basically takes in the current data and adds an ID
            ...enteredExpenseData,
            // randomizing the ID
            id: Math.random().toString()
        }
        // onAddExpense is defined in App.js
        newExpenseProps.onAddExpense(expenseData)
        setIsEditing(false)
        console.log("New entry", enteredExpenseData)
    }

    const startEditingHandler = () => {
        setIsEditing(true)
    }

    const stopEditingHandler = () => {
        setIsEditing(false)
    }

    return (
        <div className='new-expense'>
            {!isEditing && (
                <button onClick={startEditingHandler}>Add New Expense</button>
            )}
            (isEditing && (
            {/* onExpenseEntry EQUALS to ExpenseForm(expenseProps) */}
            <ExpenseForm
                onExpenseFormEntry={expenseFormEntryHandler}
                onCancel={stopEditingHandler}
            />
            ))
        </div>
    )
}

export default NewExpense
