import './NavBar.css'
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ContactLink from './ContactLink';

const NavBar = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)

    const toggleMobileMenu = () => {
        setIsMobileMenuOpen(!isMobileMenuOpen)
    }

    return (
        <nav className={`nav-bar ${isMobileMenuOpen ? 'mobile-menu-open' : ''}`}>
            <div className='nav-brand'>
                <Link to="/">GarraGigante</Link>
            </div>
            <button className='mobile-menu-button' onClick={toggleMobileMenu}>
                <span></span>
                <span></span>
                <span></span>
            </button>
            <ul className={`nav-items ${isMobileMenuOpen} ? 'mobile-menu-open' : ''}`}>
                <li><Link to="/about">Sobre nós</Link></li>
                <li><Link to="/cases">Na mídia</Link></li>
                <li><Link to="/events">A GarraGigante</Link></li>
                <li><Link to="/events">O que oferecemos</Link></li>
                <li><Link to="/contact">Contato</Link></li>
            </ul>
            <ContactLink text="Contrate já!" />
        </nav>
    )
}

export default NavBar
