import './Hero.css'
import React from 'react';
import NavBar from './NavBar'; // Import the NavBar component
import ContactLink from './ContactLink';

const Hero = () => {
    const backgroundImage = '/public/mainGif.png'

    const imageSylte = {
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        color: '#fff',
        textAlign: 'center',
        padding: '100px 0',
        // opacity: '0.3',
    };


    return (
        <div style={imageSylte} className="hero">
            <NavBar></NavBar>
            <div className="hero-content">
                <h1>Levamos a diversao até você!</h1>
                <p>Aqui somos movidos a desafios</p>
                <ContactLink text="Contrate já!"></ContactLink>
            </div>
        </div>
    );
}
export default Hero;
