import './ExpenseItem.css'

// ALTERNATIVE to props would be the actual attributes = ({date, title, price})
function ExpenseItem(itemProps) {
    const day = itemProps.date.toLocaleString('en-US', { day: '2-digit' })
    const month = itemProps.date.toLocaleString('en-US', { month: 'long' })
    const year = itemProps.date.getFullYear()
    // HARDCODED placeholder data
    // const expenseDate = new Date(2023, 7, 29)
    // const expenseTitle = "Car Insurance"
    // const expenseAmount = 294.67

    return (
        <div className='expense-item'>
            <div>
                <div>{month}</div>
                <div>{year}</div>
                <div>{day}</div>
            </div>
            <div className='expense-item__description'>
                <h2>{itemProps.title}</h2>
                <div className='expense-item__price'>${itemProps.price}</div>
            </div>
        </div>
    )
}

export default ExpenseItem
