import './ExpenseForm.css'
import { useState } from 'react'

const ExpenseForm = () => {
    // this handles each state separately
    const [enteredTitle, setEnteredTitle] = useState('')
    const [enteredPrice, setEnteredPrice] = useState('')
    const [enteredDate, setEnteredDate] = useState('')

    // this handles all states at once and requires ...(prevState) functionalities to work
    // const [userInput, setUserInput] = useState({
    //     enteredTitle: '',
    //     enteredPrice: '',
    //     enteredDate: ''
    // })

    // event is an object in which .target.value is obtained 
    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value)

        // #1 prevState alternative
        // setUserInput({
        // ...userInput is used to load current data 
        // so that the other existent entries won't be thrown away when updating a single entry
        //         ...userInput,
        //         enteredTitle: event.target.value
        //     })

        // #2prevState alternative
        // setUserInput((prevState) => {
        //     return { ...prevState, enteredTitle: event.target.value }
        // })
    }

    const priceChangeHandler = (event) => {
        setEnteredPrice(event.target.value)
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value)
    }

    // alternative to using multiple event functions
    // this requires "onChange" in the form to be modified to
    // onChange={(event) => inputChangeHandler("title", event.target.value)}
    // const inputChangeHandler = (identifier, value) => {
    //     if (identifier === 'title') {
    //         setEnteredTitle(value);
    //     } else if (identifier === 'date') {
    //         setEnteredDate(value);
    //     } else {
    //         setEnteredPrice(value);
    //     }
    // }

    const submitHandler = (event) => {
        // prevents the page reloading
        event.preventDefault()

        const expenseData = {
            title: enteredTitle,
            price: enteredPrice,
            date: new Date(enteredDate)
        }
    }

    return <form onSubmit={submitHandler}>
        <div className='new-expense__controls'>
            <div className='new-expense__control'>
                <label>Title</label>
                <input
                    type='text'
                    value={enteredTitle}
                    onChange={titleChangeHandler}
                />
            </div>
            <div className='new-expense__control'>
                <label>Price</label>
                <input
                    type='number'
                    value={enteredPrice}
                    min="0.01"
                    step="0.01"
                    onChange={priceChangeHandler}
                />
            </div>
            <div className='new-expense__control'>
                <label>Date</label>
                <input
                    type='date'
                    value={enteredDate}
                    min="2019-01-01"
                    max="2023-12-31"
                    onChange={dateChangeHandler}
                />
            </div>
        </div>
        <div className='new-expense__actions'>
            <button type='submit'>Add Expense</button>
        </div>
    </form>
}

export default ExpenseForm
