import React from 'react';
import { BrowserRouter as Router } from 'react-router-dom'

import { useState } from "react";

// import ExpenseItem from "./components/ExpenseItem";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";
import NavBar from "./components/NavBar";
import ContactLink from './components/ContactLink';
import Hero from './components/hero';

const dummyExpenses = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    price: 94.12,
    date: new Date(2020, 7, 14),
  },
  {
    id: 'e2',
    title: 'New TV',
    price: 799.49,
    date: new Date(2021, 2, 12)
  },
  {
    id: 'e3',
    title: 'Car Insurance',
    price: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    price: 450,
    date: new Date(2021, 5, 12),
  },
];

// replaces function App()
const App = () => {
  const [expenses, setExpenses] = useState(dummyExpenses)

  const addExpenseHandler = (newExpense) => {
    setExpenses((prevExpenses) => {
      return [newExpense, ...prevExpenses]
    });
  }

  const headerStyle = {
    padding: '1rem',
    fontFamily: 'Arial, sans-serif',
    fontSize: '3rem',
  }

  const bodyStyle = {
    marginLeft: '1rem',
  }

  return (
    <Router>
      <div>
      <Hero></Hero>
        <NavBar></NavBar>
        <h1 style={headerStyle}>Levamos a diversao até você!</h1>
        {/* onAddExpense is the newExpenseProp */}
        {/* <NewExpense onAddExpense={addExpenseHandler} /> */}
        {/* <Expenses items={expenses} /> */}
      </div>
      <div style={bodyStyle}>
        <ContactLink text="Contrate já!"></ContactLink>
      </div>
    </Router>


    // INADEQUATE way of passing multiple props (title, price, date)
    //   <ExpenseItem
    //     title={expenses[0, 1, 2, etc].title}
    //     price={expenses[0].price}
    //     date={expenses[0].date}
    //   ></ExpenseItem>

    // Alternative (yet INADEQUATE) way passing a single prop
    // this requires, in components.js, e.g. to be changed > {itemProps.EXPENSE.title}    
    // <ExpenseItem>
    //   expense={expenses[0, 1, 2, etc]}
    // </ExpenseItem>

    // the dynamic and proper way NOW IMPORTED to Expenses.js
    // <div>
    //   <h2>Items list</h2>
    //   {expenses.map((expense, index) => (
    //     <ExpenseItem
    //     key={index}
    //     title={expense.title}
    //     price={expense.price}
    //     date={expense.date}
    //     />
    //   ))}
    // </div>
  );
}

export default App;
