import './Expenses.css'
import { useState } from 'react';

import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import ExpenseItem from "./ExpenseItem";

const Expenses = (itemProps) => {
    const [filteredYear, setFilteredYear] = useState('2020')
    const [filterInfoText, setFilterInfoText] = useState('2019, 2021, 2022')

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear)
        if (selectedYear === '2019') {
            setFilterInfoText('2020, 2021, 2022')
        } else if (selectedYear === '2020') {
            setFilterInfoText('2019, 2021, 2022')
        } else if (selectedYear === '2021') {
            setFilterInfoText('2019, 2020, 2022')
        } else {
            setFilterInfoText('2019, 2020, 2021')
        }
    }

    // function load() {
    //     const filteredExpenses = itemProps.items.map((expense) => (
    //         console.log("haha", expense)
    //     ))
    
    // }

    const whateverExpenses = [
        { id: '12', title: 'Eins eins', year: '2020' }
    ];

    // const [expenses, setExpenses] = useState(whateverExpenses);

    // const filterExpensesByYear = (yearToFilter) => {
    //     const filteredExpenses = whateverExpenses.filter((dummy) => {
    //         return dummy.year === yearToFilter
    //     });
    //     setExpenses(filteredExpenses);
    // }

    // filterExpensesByYear('2020')

    // function load() {
    //     console.log("he")
    // }

    // load()
  

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter
                    selected={filteredYear}
                    onChangeFilter={filterChangeHandler}
                />
                {/* e.g. 2019, 2020, & 2022 */}
                <p>Data for years {filterInfoText} is hidden.</p>
                {/*"itemProps.items" replaces declaring a "const expenses = itemProps.items"*/}
                {itemProps.items.map((expense) => (
                    <ExpenseItem
                        // ultimately, in the absence of ID
                        // index could be used but it is strongly discouraged
                        key={expense.id}
                        title={expense.title}
                        price={expense.price}
                        date={expense.date}
                    />
                ))}
            </Card>
        </div>
    )
}

export default Expenses
