import './NavBar.css'
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ContactLink from './ContactLink';

const NavBar = () => {
    const [isSubMenuOpen, setIsSubMenuOpen] = useState(false)

    const handleSubMenuClick = (event) => {
        setIsSubMenuOpen(!isSubMenuOpen)
        event.preventDefault()
    }

    return (
        <nav className='nav-bar'>
            <div>GarraGigante</div>

            <button
                className='sub-menu-button'
                onClick={handleSubMenuClick}
            >
                <i className='fas fa-bars'></i>
            </button>

            {isSubMenuOpen && (
                <ul className='nav-items'>
                    <li><Link to="/about">Sobre nós</Link></li>
                    <li><Link to="/media">Na mídia</Link></li>
                    <li><Link to="/garra">A GarraGigante</Link></li>
                    <li><Link to="/services">O que oferecemos</Link></li>
                    <li><Link to="/contact">Contato</Link></li>
                </ul>
            )}
            <ContactLink className="contact-link" text="Contrate já!" />
        </nav>
    )
}

export default NavBar
