import './ExpenseItem.css'
import { useState } from 'react'

import Card from '../UI/Card'
import ExpenseDate from './ExpenseDate'

// ALTERNATIVE to simply props would be the actual attributes = ({date, title, price})
const ExpenseItem = (itemProps) => {
    // HARDCODED placeholder data
    // const expenseDate = new Date(2023, 7, 29)
    // const expenseTitle = "Car Insurance"
    // const expenseAmount = 294.67

    useState()
    
    let title = itemProps.title

    const clickHandler = () => {
        title = "Updated"
    }

    return (
        <Card className='expense-item'>
            <ExpenseDate date={itemProps.date} />
            <div className='expense-item__description'>
                <h2>{title}</h2>
                <div className='expense-item__price'>${itemProps.price}</div>
            </div>
            <button onClick={clickHandler}>Edit Title</button>
        </Card>
    )
}

export default ExpenseItem
