import './NavBar.css'
import React from 'react';
import { Link } from 'react-router-dom';
import ContactLink from './ContactLink';

const NavBar = () => {
    const subMenuButton = document.querySelector('.sub-menu-button');
    const navItems = document.querySelector('.nav-items');

    subMenuButton.addEventListener('click', () => {
        navItems.classList.toggle('active');
    });

    return (
        <nav className='nav-bar'>
            <div>GarraGigante</div>
            <ul className='nav-items'>
                <li><Link to="/about">Sobre nós</Link></li>
                <li><Link to="/cases">Na mídia</Link></li>
                <li><Link to="/events">A GarraGigante</Link></li>
                <li><Link to="/events">O que oferecemos</Link></li>
                <li><Link to="/contact">Contato</Link></li>
            </ul>
            <ContactLink className="contact-link" text="Contrate já!" />
        </nav>
    )
}

export default NavBar
