    //   <h2>Items list</h2>
    //   {expenses.map((expense, index) => (
    //     <ExpenseItem
    //     key={index}
    //     title={expense.title}
    //     price={expense.price}
    //     date={expense.date}
    //     />
    //   ))}
    // </div>
  );
}

export default App;
