import './NavBar.css'
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ContactLink from './ContactLink';

const NavBar = () => {
    const [isSubMenuOpen, setIsSubMenuOpen] = useState(false)
    const [isSmallScreen, setIsSmallScreen] = useState(false)

    const toggleSubMenu = () => {
        setIsSubMenuOpen(!isSubMenuOpen)
    }

    useEffect(() => {
        // Check the viewport width when the component mounts and when it resizes
        const checkScreenWidth = () => {
            setIsSmallScreen(window.innerWidth <= 580);
        };

        checkScreenWidth(); // Initial check
        window.addEventListener("resize", checkScreenWidth);

        return () => {
            // Cleanup the event listener when the component unmounts
            window.removeEventListener("resize", checkScreenWidth);
        };
    }, []);

    return (
        <nav className='nav-bar'>
            <header className='nav-header'>
                GarraGigante
            </header>

            <button
                className='sub-menu-button'
                onClick={toggleSubMenu}
                aria-expanded={isSubMenuOpen}
                aria-label='Toggle Menu'
            >
                <i className='fas fa-bars'></i>
            </button>

            {(!isSmallScreen || isSubMenuOpen) && (
                <ul className='nav-items'>
                    <li><Link to="/about">Sobre nós</Link></li>
                    <li><Link to="/media">Na mídia</Link></li>
                    <li><Link to="/garra">A GarraGigante</Link></li>
                    <li><Link to="/services">Servicos</Link></li>
                    <li><Link to="/contact">Contato</Link></li>
                </ul>
            )}
            <ContactLink className="contact-link" text="Contrate já!" />
        </nav>
    )
}

export default NavBar
