import './ExpensesList.css';

import ExpenseItem from './ExpenseItem';

const ExpensesList = propsFromList => {
    if (propsFromList.itemsFromList.length === 0) {
        return <h2 className='expenses-list__fallback'>No expenses found.</h2>
    }

    return (
        <ul className='expenses-list'>
            {propsFromList.itemsFromList.map((expense) => (
                <ExpenseItem
                    // ultimately, in the absence of ID
                    // index could be used but it is strongly discouraged
                    key={expense.id}
                    title={expense.title}
                    price={expense.price}
                    date={expense.date}
                />
            ))}
        </ul>
    )
}

export default ExpensesList
