import './Expenses.css'

import Card from '../UI/Card';
import ExpenseItem from "./ExpenseItem";
import { useState } from 'react';

const Expenses = (itemProps) => {
    const [filteredYear, setFilteredYear] = useState('2020')
    const [filterInfoText, setFilterInfoText] = useState('2019, 2021, & 2022')

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear)
        if (selectedYear === '2019') {
            setFilterInfoText('2020, 2021, 2022')
        } else if (selectedYear === '2020') {
            setFilterInfoText('2019, 2021, 2022')
        } else if (selectedYear === '2ß21') {
            setFilterInfoText('2019, 2020, 2022')
        } else {
            setFilterInfoText('2019, 2020, 2021')
        }
    }
    
    return (
        <Card className="expenses">
            {/*"itemProps.items" replaces declaring a "const expenses = itemProps.items"*/}
            {itemProps.items.map((expense, index) => (
                <ExpenseItem
                    key={index}
                    title={expense.title}
                    price={expense.price}
                    date={expense.date}
                />
            ))}
        </Card>
    )
}

export default Expenses
